package com.digitalinclined.edugate.ui.fragments.mainactivity

import android.Manifest
import android.content.Intent
import android.content.pm.PackageManager
import android.os.Bundle
import android.os.Environment
import android.util.Base64
import android.util.Log
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.TextView
import androidx.core.app.ActivityCompat
import androidx.core.content.ContextCompat
import androidx.core.net.toUri
import androidx.fragment.app.Fragment
import androidx.navigation.fragment.navArgs
import com.digitalinclined.edugate.R
import com.digitalinclined.edugate.constants.Constants.BASE_64_STRING
import com.digitalinclined.edugate.databinding.FragmentPDFBinding
import com.digitalinclined.edugate.ui.fragments.MainActivity
import java.io.ByteArrayInputStream
import java.io.File
import java.io.FileOutputStream
import java.io.InputStream


class PDFFragment : Fragment() {

    // viewBinding
    private var _binding: FragmentPDFBinding? = null
    private val binding get() = _binding!!

    // nav args
    private val args: PDFFragmentArgs by navArgs()

    // previous Fragment
    var previousFragment: String? = null
    var fragmentPdfName = ""
    var pdfUri = ""

    override fun onCreateView(
        inflater: LayoutInflater,
        container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        _binding = FragmentPDFBinding.inflate(inflater, container, false)

        // getting values from args
        previousFragment = args.previousFragment
        fragmentPdfName = args.fragmentPdfName
        pdfUri = args.pdfUri

        // change the title bar
        (activity as MainActivity).findViewById<TextView>(R.id.toolbarTitle).text = "PDF Viewer"

        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        binding.apply {

            // pdf view
            if(previousFragment != null) {
                when (previousFragment) {
                    "SyllabusFragment" -> {
                        pdfView.fromAsset("Eng_Core_pdf.pdf").load()
                    }
                    "NotesFragment" -> {
                        pdfView.fromAsset("machine_learning.pdf").load()
                    }
                    "PreviousYearPapersFragment" -> {
                        if (fragmentPdfName == "paper") {
                            pdfView.fromAsset("history_paper.pdf").load()
                        }else {
                            pdfView.fromAsset("history_syllabus.pdf").load()
                        }
                    }
                    "AddDiscussionFragment" -> {
                        pdfView.fromUri(pdfUri.toUri())
                            .defaultPage(0)
                            .spacing(10)
                            .load()                    }
                }
            }
        }
    }

    // saving file to external storage
    fun saveToExternalStorage() {
        val fullPath = Environment.getExternalStorageDirectory().absolutePath + "/directoryName";

        var string64 = ""

        var byteArray: ByteArray = Base64.decode(string64,Base64.NO_WRAP)

        Log.d("TAGU", Environment.getExternalStorageDirectory().absolutePath.toString())

        try {
            var dir = File(fullPath);
            if (!dir.exists()) {
                dir.mkdirs();
            }
            var file = File(fullPath, "fileName3.pdf")
            if (file.exists()) {
                file.delete()
            }
            file.createNewFile()
            var fOut = FileOutputStream(file)
            fOut.write(byteArray)
            fOut.flush()
            fOut.close()
        } catch (e: Exception) {
            Log.e("saveToExternalStorage()", e.message.toString())
        }
    }

    override fun onRequestPermissionsResult(
        requestCode: Int,
        permissions: Array<out String>,
        grantResults: IntArray
    ) {
        super.onRequestPermissionsResult(requestCode, permissions, grantResults)
        when(requestCode) {
            1119 -> {
//                val file = File(
//                    Environment.getExternalStorageDirectory().path,
//                    "ParentFolder/Report"
//                )
                val file = File("/document/10536JankiDevi.PDF")
                if (!file.exists()) {
                    file.mkdirs()
                }
                val pdfAsBytes: ByteArray = Base64.decode(BASE_64_STRING,Base64.NO_WRAP)
                val os: FileOutputStream = FileOutputStream(file, false)
                os.write(pdfAsBytes)
                os.flush()
                os.close()
            }
        }
    }

    override fun onDestroy() {
        super.onDestroy()
        _binding = null
    }

}